import React, { useState, useEffect } from "react";
import { Text, FlatList} from "react-native";
import RepositoryItem from "./RepositoryItem.jsx";

const RepositoryList = () =>{
    const [repositories, setRepositories] = useState([""]);

    const fetchedRespositories = async () => {
        const res = await fetch("http://localhost:5000/api/repositories")
        const json = await res.json()
        setRepositories(json)
    }

    useEffect(() =>{
        fetchedRespositories()
    }, [])

    const respositoriesNodes = repositories ?
        repositories.edge.map(edge => edge.node) : []


    return(
        <FlatList 
            data={respositoriesNodes}
            ItemSeparatorComponent={() => <Text></Text>}
            renderItem ={({item : repo}) => (
                <RepositoryItem {...repo}/>
            )}
        />
    )
}

export default RepositoryList
